def shortestDistanceAfterQueries2(n: int, queries: List[List[int]]) -> List[int]:
    ans = []
    nxt = list(range(1, n))
    cnt = n - 1
    for l, r in queries:
        if 0 < nxt[l] < r:
            i = nxt[l]
