vector<int> shortestDistanceAfterQueries2(int n, vector<vector<int>>& queries) {
        vector<int> nxt(n - 1);
        iota(nxt.begin(), nxt.end(), 1);
        vector<int> ans(queries.size());
        int cnt = n - 1;
        for (int qi = 0; qi < queries.size(); qi++) {
            int l = queries[qi][0], r = queries[qi][1];
            if (nxt[l] && nxt[l] < r) {
                for (int i = nxt[l]; i < r;) {
                    cnt--;
                    int tmp = nxt[i];
                    nxt[i] = 0;
                    i = tmp;
                }
                nxt[l] = r;
            }
            ans[qi] = cnt;
        }
        return ans;
    }

