def getMax( x: int) -> int:

        flag = False
        prev = -1
        cnt = 0
        for i in range(30, -1, -1):
            if cnt >= 2:
                break
            if not flag and (x & (1 << i)) > 0:
                flag = True
                continue
            if flag:
                if (x & (1 << i)) == 0 and prev == -1:
