int getMax(int x) {

    bool flag = false;
    int prev = -1;
    int cnt = 0;
    for (int i = 30; i >= 0; i--) {
        if (cnt >= 2)
            break;
        if (flag == false and (x & (1 << i)) > 0) {
            flag = true;
            continue;
        }
        if (flag) {
            if ((x & (1 << i)) == 0 and prev == -1) {
                prev = i;
                x |= (1 << i);
                cnt++;
            }
            if ((x & (1 << i)) == 0 and prev - i > 1) {
                x |= (1 << i);
                cnt++;
            }
        }
    }
    return x;
}

