def minSwaps( S):
        even_sum, odd_sum = 0, 0
        n = len(S)

        for i in range(n):
            if i % 2 == 1:
                odd_sum += (S[i] == '1')
            else:
                even_sum += (S[i] == '1')

        difference = abs(odd_sum - even_sum)

        if difference % 2 == 1:
            return -1

        return difference // 2

