int minSwaps(string S) {
        int evenSum = 0, oddSum = 0;
        int n = S.size();

        for (int i = 0; i < n; i++) {
            if (i & 1)
                oddSum += (S[i] == '1');
            else
                evenSum += (S[i] == '1');
        }

        int difference = abs(oddSum - evenSum);

        if (difference & 1)
            return -1;

        return difference / 2;
    }

