def leftmostBuildingQueries(heights: List[int], queries: List[List[int]]) -> List[int]:
    n = len(heights)
    m = len(queries)
    query = [[] for _ in range(n)]
    ans = [-1] * m
    st = []
    for i in range(m):
        a, b = queries[i]
        if a > b:
            a, b = b, a
        if a == b or heights[a] < heights[b]:
            ans[i] = b
            continue
        query[b].append((i, heights[a]))
    top = -1
    for i in range(n - 1, -1, -1):
        for q, val in query[i]:
            if top == -1 or heights[st[0]] <= val:
                ans[q] = -1
                continue
            l, r = 0, top
            while l <= r:
                mid = (l + r) // 2
                if heights[st[mid]] > val:
                    l = mid + 1
                else:
                    r = mid - 1
            ans[q] = st[r]
        while top >= 0 and heights[st[top]] <= heights[i]:
            st.pop()
            top -= 1
        st.append(i)
        top += 1
    return ans

