def countPow(x):
    res = 0
    sum = 0

    for i in range(60, -1, -1):
        if (1 << i) & x:
            res += sum * (1 << i)
            sum += i

            if i > 0:
                res += i * (i - 1) // 2 * (1 << (i - 1))

    res += sum
    return res

