long long countPow(long long x) {
        long long res = 0;
        int sum = 0;

        for (int i = 60; i >= 0; i--) {
            if (1LL << i & x) {
                res += 1LL * sum * (1LL << i);
                sum += i;

                if (i > 0) {
                    res += 1LL * i * (i - 1) / 2 * (1LL << (i - 1));
                }
            }
        }
        res += sum;
        return res;
    }

