    int ans = 0;
    for (int x: nums) {
        int mx = 0, base = 0;
        for (; x; x /= 10) {
            mx = max(mx, x % 10);
            base = base * 10 + 1;
        }
        ans += mx * base;
    }
    return ans;
}

