def maxScoreSubseq( n : int, arr : List[int]) -> int:
    d=defaultdict(int)
    v=max(arr)
    for i in range(n):
        d[arr[i]-i]+=max(0,arr[i])
    c=(1 if 0 in arr else 0)
    for el in d:
        if d[el]>0:
            v=max(v,d[el])
        elif d[el]==0 and c:
            v=max(0,v)
    return v

