int maxScoreSubseq(int n, vector<int> &arr) {
    unordered_map<int,int>mp;
    int ma=INT_MIN;
    for (int i=0;i<n;i++){
        if (arr[i]>=0)mp[arr[i]-i]+=arr[i];
        ma=max(ma,arr[i]);
    }
    int ele=INT_MIN;
    for (auto j:mp) ele=max(ele,j.second);
    return max(ele,ma);
}

