long long validPairs(int n, vector<int>& val, vector<vector<int>>& edges) {
    vector<vector<long long>> lift(n + 1, vector<long long>(32, 0));
    vector<long long> dis(n + 1, 0);
    vector<long long> dis2(n + 1, 0);
    vector<vector<vector<long long>>> adj(n + 1);
    for (auto i : edges) {
        adj[i[0]].push_back({ i[1], i[2] });
        adj[i[1]].push_back({ i[0], i[2] });
    }
    dis[0] = -1e15;

    stack<vector<long long>> st;
    st.push({ 1, 0, 0, 0 }); 
    while (!st.empty()) {
        auto top = st.top();
        st.pop();
        long long i = top[0], par = top[1], d = top[2], d2 = top[3];
        lift[i][0] = par;
        dis[i] = d;
        dis2[i] = d2;
        for (long long j = 1; j < 32; j++) {
            lift[i][j] = lift[lift[i][j - 1]][j - 1];
        }
        for (auto j : adj[i]) {
            if (j[0] == par) continue;
            st.push({ j[0], i, d + j[1], d2 + 1 });
        }
    }
    long long ans = 0;
    for (long long i = 1; i <= n; i++) {
        long long v = val[i-1];
        long long p = i;
        for (long long j = 31; j >= 0; j--) {
            if (v >= dis[p] - dis[lift[p][j]]) {
                v -= (dis[p] - dis[lift[p][j]]);
                p = lift[p][j];
            }
        }
        ans += dis2[i] - dis2[p];
    }
    return ans;
}

