    ArrayList<Integer> hsh = new ArrayList<>(26);
    for (int i = 0; i < 26; i++) hsh.add(0);
    for (Character c : s.toCharArray()) hsh.set(c - 'a', hsh.get(c - 'a') + 1);
    int curr = 0;
    for (int i = 0; i < 26; i++)
        if (hsh.get(i) > 0) curr++;
    if (curr == 1) return "NO";
    return "YES";
