public static int maxPalindromesAfterOperations(String[] words) {
    int tot = 0;
    int mask = 0; 
    for (String w : words) {
        tot += w.length();
        for (char c : w.toCharArray()) {
            mask ^= 1 << (c - 'a');
        }
    }
    tot -= Integer.bitCount(mask); 
    Arrays.sort(words, (a, b) -> a.length() - b.length());
    int ans = 0;
    for (String w : words) {
        tot -= w.length() / 2 * 2; 
        if (tot < 0) break;
        ans++;
    }
    return ans;
}

