public static int longestCommonPrefix(int[] arr1, int[] arr2) {
    Set<String> st = new HashSet<>();
    for (int x : arr1) {
        String s = Integer.toString(x);
        for (int i = 1; i <= s.length(); i++) {
            st.add(s.substring(0, i));
        }
    }
    int ans = 0;
    for (int x : arr2) {
        String s = Integer.toString(x);
        for (int i = 1; i <= s.length(); i++) {
            if (!st.contains(s.substring(0, i))) {
                break;
            }
            ans = Math.max(ans, i);
        }
    }
    return ans;
}

