public static long maximumStrength(int[] nums, int k) {
    int n=nums.length;
    long ans[][][]=new long[n+1][k+1][2];
    for(int j=0;j<=k;j++){ans[0][j]=new long[]{-(long)1e18,-(long)1e18};}
    ans[0][0][0]=0;
    for(int i=1;i<=n;i++){
        ans[i][0][1]=-(long)1e14;
        for(int j=1;j<=k;j++){
            ans[i][j][0]=Math.max(ans[i-1][j][0],ans[i-1][j][1]);
            ans[i][j][1]=Math.max(ans[i-1][j-1][1],Math.max(ans[i-1][j-1][0],ans[i-1][j][1]))+(long)nums[i-1]*(j%2*2-1)*(k+1-j);
        }
    }
    return Math.max(ans[n][k][0],ans[n][k][1]);
}

