public static int maxSelectedElements(int[] nums) {
    Arrays.sort(nums);
    Map<Integer, Integer> f = new HashMap<>();
    for (int x : nums) {
        f.put(x + 1, f.getOrDefault(x, 0) + 1);
        f.put(x, f.getOrDefault(x - 1, 0) + 1);
    }
    int ans = 0;
    for (int res : f.values()) {
        ans = Math.max(ans, res);
    }
    return ans;
}

