def minOperations2( nums: List[int]) -> int:
    ans = 0
    for i in range(len(nums) - 2):
        if nums[i] == 0:
            nums[i + 1] ^= 1
            nums[i + 2] ^= 1
            ans += 1
    return ans if nums[-2] and nums[-1] else -1

reference_code:
public static int minOperations2(int[] nums) {
    int n = nums.length;
    int ans = 0;
    for (int i = 0; i < n - 2; i++) {
        if (nums[i] == 0) {
            nums[i + 1] ^= 1;
            nums[i + 2] ^= 1;
            ans++;
        }
    }
    return nums[n - 2] != 0 && nums[n - 1] != 0 ? ans : -1;
}

