def minimumArrayLength( nums: List[int]) -> int:
    m = min(nums)
    for x in nums:
        if x % m:
            return 1
    return (nums.count(m) + 1) // 2

reference_code:
public static int minimumArrayLength(int[] nums) {
    int m = Integer.MAX_VALUE;
    for (int x : nums) {
        m = Math.min(m, x);
    }
    for (int x : nums) {
        if (x % m > 0) {
            return 1;
        }
    }
    int cnt = 0;
    for (int x : nums) {
        if (x == m) {
            cnt++;
        }
    }
    return (cnt + 1) / 2;
}

