public static int longestMonotonicSubarray(int[] a) {
    int ans = 1;
    int i = 0, n = a.length;
    while (i < n - 1) {
        if (a[i + 1] == a[i]) {
            i++; 
            continue;
        }
        int i0 = i; 
        boolean inc = a[i + 1] > a[i]; 
        i += 2; 
        while (i < n && a[i] != a[i - 1] && (a[i] > a[i - 1]) == inc) {
            i++;
        }

        ans = Math.max(ans, i - i0);
        i--;
    }
    return ans;
}

