public static int numberOfPairs(int[][] points) {
    Arrays.sort(points, (p, q) -> p[0] != q[0] ? p[0] - q[0] : q[1] - p[1]);
    int ans = 0;
    for (int i = 0; i < points.length; i++) {
        int y0 = points[i][1];
        int maxY = Integer.MIN_VALUE;
        for (int j = i + 1; j < points.length; j++) {
            int y = points[j][1];
            if (y <= y0 && y > maxY) {
                maxY = y;
                ans++;
            }
        }
    }
    return ans;
}

