def getMaxProductivity( n: int, productivity: List[int]) -> int:
        total_sum = sum(productivity)

        dp = [0] * (total_sum + 1)
        dp[0] = 1

        for prod in productivity:
            for j in range(total_sum, prod - 1, -1):
                dp[j] = dp[j] or dp[j - prod]

        max_productivity = 0
        for i in range(1, total_sum):
            if dp[i]:
                max_productivity = max(max_productivity, i * (total_sum - i))
        return max_productivity

