def maximumLength( nums: List[int], k: int) -> int:
    f = [[0] * k for _ in range(k)]
    for x in nums:
        x %= k
        for y, fxy in enumerate(f[x]):
            f[y][x] = fxy + 1
    return max(map(max, f))

reference_code:
public static int maximumLength(int[] nums, int k) {
    int ans = 0;
    int[][] f = new int[k][k];
    for (int x : nums) {
        x %= k;
        for (int y = 0; y < k; y++) {
            f[y][x] = f[x][y] + 1;
            ans = Math.max(ans, f[y][x]);
        }
    }
    return ans;
}

