def canSortArray( nums: List[int]) -> bool:
    lastCnt = 0
    lastGroupMax = 0
    curGroupMax = 0
    for num in nums:
        curCnt = num.bit_count()
        if curCnt == lastCnt:
            curGroupMax = max(curGroupMax, num)
        else:
            lastCnt = curCnt
            lastGroupMax = curGroupMax
            curGroupMax = num
        if num < lastGroupMax:
            return False
    return True

reference_code:
public static boolean canSortArray(int[] nums) {
    int lastCnt = 0;
    int lastGroupMax = 0;
    int curGroupMax = 0;
    for (int num : nums) {
        int curCnt = Integer.bitCount(num);
        if (curCnt == lastCnt) {
            curGroupMax = Math.max(curGroupMax, num);
        } else {
            lastCnt = curCnt;
            lastGroupMax = curGroupMax;
            curGroupMax = num;
        }
        if (num < lastGroupMax) {
            return false;
        }
    }
    return true;
}

