public static int mostFrequent(int a[], int n)
    {
        HashMap<Integer, Integer> mp = new HashMap<>();
        for(int i=0;i<n;i++)
        {
            if(mp.containsKey(a[i]))
                mp.put(a[i], mp.get(a[i])+1);
            else
                mp.put(a[i],1);
        }
        int ans=-1,res=0;
        for(Entry<Integer,Integer> val : mp.entrySet())
            res=Math.max(res,val.getValue());
        for(Entry<Integer,Integer> val : mp.entrySet())
        {
            if (val.getValue()==res&&val.getKey()>ans)
                ans=val.getKey();
        }
        return ans;
    }

