static long findGoodPairs(int a[], int n, int k){

    long ans = 0;
    Map<Integer, Integer> hm = new HashMap<>();
    for(int i = k; i < n; i++){
        hm.put(a[i - k], hm.getOrDefault(a[i - k], 0) + 1);
        ans += hm.getOrDefault(a[i], 0);
    }
    return ans;
}

