public static int HeightTower(int n, int k, int[] arr) {

    Stack<Integer> st = new Stack<>();

    int totalHeight = 0;

    int i = 0;

    for (int j = 0; j < n; j++) {
        totalHeight += arr[j];
        st.push(arr[j]);

        if (totalHeight > k) {
            int removeBoxes = i + 1;

            while (removeBoxes > 0 && !st.empty()) {
                totalHeight -= st.pop();
                removeBoxes--;
            }
            i++;
        }
    }

    return totalHeight;
}

