public static String trafficLights(int n, int q, int[][] queries) {
    int[] darr = new int[n + 1];
    for (int[] Q : queries) {
        int a = Q[0];
        int b = Q[1];
        darr[a - 1]++;
        darr[b]--;
    }
    Arrays.parallelPrefix(darr, (A, B) -> A + B);
    StringBuilder sb = new StringBuilder();
    for (int i = 0; i < n; i++) {
        int X = darr[i] % 3;
        if (X == 0)
            sb.append("R");
        else if (X == 1)
            sb.append("Y");
        else if (X == 2)
            sb.append("G");
    }
    return sb.toString();
}

reference_code:
def trafficLights( n: int, q: int, queries: List[List[int]]) -> str:
    sweap = [0] * (n + 1)
    for l, r in queries:
        sweap[l - 1] += 1
        sweap[r] -= 1
    curr = 0
    ans = ""
    for i in range(n):
        curr = (curr + sweap[i]) % 3
        if curr == 0:
            ans += 'R'
        elif curr == 1:
            ans += 'Y'
        else:
            ans += 'G'
    return ans

