public static long ForYou(int n, int[] arr, int m, int[][] order) {

    int[] diff = new int[n + 1];

    for (int[] it : order) {
        diff[it[0] - 1]++;
        diff[it[1]]--;
    }

    for (int i = 1; i <= n; i++) {
        diff[i] += diff[i - 1];
    }

    diff = Arrays.copyOf(diff, n);
    Arrays.sort(diff);

    Arrays.sort(arr);
    long ans = 0;
    int mod = (int)1e9 + 7;

    for (int i = 0; i < n; i++) {
        ans += ((long)diff[i]) * ((long)arr[i]);
        ans %= mod;
    }
    return ans;
}

