reference_code:
def smallestString( n : int, s : str) -> str:
    #return -1 if n is 1
    if n == 1:
        return "-1"
    answer = list(s)
    #iterate only till half of the string
    for i in range(n // 2):
        #if character is not 'a', replace it with 'a' and return
        if s[i] != 'a':
            answer[i] = 'a'
            return ''.join(answer)
    #if all characters are 'a', change the last character to 'b'
    answer[-1] = 'b'
    return ''.join(answer)

