def matchingCnt( n: int, X: List[str]) -> List[int]:
    mp = []
    ans = [0] * n
    for i in range(n - 1, -1, -1):
        s = X[i]
        for j in range(len(s)):
            #if the length of mp is equal to current index, add a new row.
            if len(mp) == j:
                mp.append([0] * 26)
            #update the answer with the count of matching characters.
            ans[i] += mp[j][ord(s[j]) - 97]
            mp[j][ord(s[j]) - 97] += 1
    return ans

