def countSuperPalindrome( l: int, r: int) -> int:
    count = 0
    start = ceil(sqrt(l))
    while start * start <= r:
        s = str(start * start)
        #Checking if the square of start is a palindrome.
        if s == s[::-1]:
            count += 1
        start += 1
    return count

