def minJumps2( n, arr):
    right_greater = [n] * n
    right_lesser = [n] * n
    stack = []
    for i in range(n):
        while stack and arr[stack[-1]] < arr[i]:
            right_greater[stack[-1]] = i
            stack.pop()
        stack.append(i)
    stack.clear()
    for i in range(n):
        while stack and arr[stack[-1]] > arr[i]:
            right_lesser[stack[-1]] = i
            stack.pop()
        stack.append(i)
    queue = deque([(0, 0)])
    visited = [0] * n
    while queue:
        p, d = queue.popleft()
        if visited[p] == 1:
            continue
        visited[p] = 1
        if p == n - 1:
            return d
        temp = p + 1
        # max(arr[i], arr[j]) < min(arr[i+1], arr[i+2], ......, arr[j-2], arr[j-1])
        if arr[temp] > arr[p]:
            while temp != n and arr[temp] > arr[p]:
                queue.append((temp, d + 1))
                temp = right_lesser[temp]
            if temp != n:
                queue.append((temp, d + 1))
        # min(arr[i], arr[j]) < max(arr[i+1], arr[i+2], ......, arr[j-2], arr[j-1])
        else:
            while temp != n and arr[temp] < arr[p]:
                queue.append((temp, d + 1))
                temp = right_greater[temp]
            if temp != n:
                queue.append((temp, d + 1))
    return -1

