reference_code:
def geekTasks( n : int, m : int, q : int, tasks : List[List[int]]) -> int:
    minA=0
    minB=0
    for i in range(q):
        a,b=tasks[i][0],tasks[i][1]
        a+=1
        b+=1
        if i==0:
            minA=a
            minB=b
        else:
            if minA>a:
                minA=a
            if minB>b:
                minB=b
    total=0
    maxi=0
    col=[0]*(n)
    for i in range(q):
        col[tasks[i][0]]=max(col[tasks[i][0]],tasks[i][1]+1)
    for i in range(n-1,-1,-1):
        maxi=max(maxi,col[i])
        total+=maxi
    total-=minA*minB
    return total

