public static int areaOfMaxDiagonal(int[][] dimensions) {
    int ans = 0, maxL = 0;
    for (int[] d : dimensions) {
        int x = d[0], y = d[1];
        int l = x * x + y * y;
        if (l > maxL || (l == maxL && x * y > ans)) {
            maxL = l;
            ans = x * y;
        }
    }
    return ans;
}

