def longestCommonPrefix( arr1: List[int], arr2: List[int]) -> int:
    st = set()
    for s in map(str, arr1):
        for i in range(1, len(s) + 1):
            st.add(s[:i])
    ans = 0
    for s in map(str, arr2):
        for i in range(1, len(s) + 1):
            if s[:i] not in st:
                break
            ans = max(ans, i)
    return ans

