reference_code:
def minimumLevels( possible: List[int]) -> int:
    # cnt1 - cnt0 = cnt1 - (n - cnt1) = cnt1 * 2 - n
    s = sum(possible) * 2 - len(possible)
    pre = 0
    for i, x in enumerate(possible[:-1]):
        pre += 2 if x else -2
        if pre > s:
            return i + 1
    return -1

