public static long findMaximumElegance(int[][] items, int k) {
    Arrays.sort(items, (item0, item1) -> item1[0] - item0[0]);
    var categorySet = new HashSet<Integer>();
    long profit = 0, res = 0;
    var st = new ArrayDeque<Integer>();
