public static int distinctIntegers(int n) {
    int[] nums = new int[n + 1];
    nums[n] = 1;
    for (int k = 0; k < n; k++) {
        for (int x = 1; x <= n; x++) {
            if (nums[x] == 0) {
                continue;
            }
            for (int i = 1; i <= n; i++) {
                if (x % i == 1) {
