    int left = 1, right = 200000000, ans = 0;
    while (left <= right) {
        int mid = (left + right) / 2;
        boolean valid = false;
        for (int i = 0; i < k; ++i) {
            long spend = 0;
            for (int j = 0; j < n; ++j) {
                spend += Math.max((long) composition.get(i).get(j) * mid - stock.get(j), 0) * cost.get(j);
            }
            if (spend <= budget) {
                valid = true;
                break;
            }
        }
        if (valid) {
            ans = mid;
            left = mid + 1;
        } else {
            right = mid - 1;
        }
    }
    return ans;
}

