public static long maxAlternatingSubstring(int N, String S) {
    List<Integer> diff = new ArrayList<>();
    for (int i = 0; i < N;) {
        int j = i + 1;
        while (j < N && S.charAt(j) != S.charAt(j - 1)) j++;
        diff.add(j - i);
        i = j;
    }
    diff.add(0);
    long ans_before = 0;
    for (int x : diff) {
        ans_before += ((x * 1L * (x + 1)) / 2);
    }
    long ans = 0;
    for (int i = 0; i < diff.size() - 1; i++) {
        long a = diff.get(i);
        long b = diff.get(i + 1);
        long currAns = ans_before - ((a * (a + 1)) / 2) - ((b * (b + 1)) / 2) +
                       ((a + b + 1) * (a + b + 2)) / 2;
        ans = Math.max(ans, currAns);
    }
    return ans;
}

