public static long validPairs(int n, int[] val, int[][] edges) {

    long[][] lift = new long[n + 1][32];
    long[] dis = new long[n + 1];
    long[] dis2 = new long[n + 1];
    List<List<long[]>> adj = new ArrayList<>();

    for(int i = 0; i <= n; i++){
        adj.add(new ArrayList<>());
    }

    for (int i = 0; i < edges.length; i++) {
        adj.get(edges[i][0]).add(new long[]{ edges[i][1], edges[i][2] });
        adj.get(edges[i][1]).add(new long[]{ edges[i][0], edges[i][2] });
    }
    dis[0] = (long)-1e15;

    Stack<long[]> st = new Stack<>();
    st.push(new long[]{ 1, 0, 0, 0 }); 
    while (!st.isEmpty()) {
        long[] top = st.pop();
        long i = top[0], par = top[1], d = top[2], d2 = top[3];
        lift[(int)i][0] = par;
        dis[(int)i] = d;
        dis2[(int)i] = d2;
        for (long j = 1; j < 32; j++) {
            lift[(int)i][(int)j] = lift[(int)lift[(int)i][(int)(j - 1)]][(int)j - 1];
        }
        for (long[] j : adj.get((int)i)) {
            if (j[0] == par) continue;
            st.push(new long[]{ j[0], i, d + j[1], d2 + 1 });
        }
    }

    long ans = 0;
    for (long i = 1; i <= n; i++) {
        long v = val[(int)i - 1];
        long p = i;
        for (long j = 31; j >= 0; j--) {
            if (v >= dis[(int)p] - dis[(int)lift[(int)p][(int)j]]) {
                v -= (dis[(int)p] - dis[(int)lift[(int)p][(int)j]]);
                p = lift[(int)p][(int)j];
            }
        }
        ans += dis2[(int)i] - dis2[(int)p];
    }
    return ans;
}

