reference_code:
def minOrAfterOperations( nums: List[int], k: int) -> int:
    ans = mask = 0
    for b in range(max(nums).bit_length() - 1, -1, -1):
        mask |= 1 << b
        cnt = 0  # 操作次数
        and_res = -1  # -1 的二进制全为 1
        for x in nums:
            and_res &= x & mask
            if and_res:
                cnt += 1  # 合并 x，操作次数加一
            else:
                and_res = -1  # 准备合并下一段
        if cnt > k:
            ans |= 1 << b  # 答案的这个比特位必须是 1
            mask ^= 1 << b  # 后面不考虑这个比特位
    return ans

