def minOrAfterOperations(nums, k):
    mask = 0
    count = 0
    for b in range(32):
        mask |= (1 << b)
        count += 1
        for x in nums:
            if mask & (1 << b) == 0:
                count += 1
                mask = mask ^ x & mask
            else:
                count += 1
                mask = mask | x & mask
    if count > k:
        ans = (mask >> b) & 1
    else:
        ans = 0
    return ans

