def minimumArea( grid: List[List[int]]) -> int:
    left, right = len(grid[0]), 0
    top, bottom = len(grid), 0
    for i, row in enumerate(grid):
        for j, x in enumerate(row):
            if x:
                left = min(left, j)
                right = max(right, j)
                top = min(top, i)
                bottom = i
    return (right - left + 1) * (bottom - top + 1)

