public static ArrayList<ArrayList<Integer>> findEnemies(int n, int m, int k, int[] col, int[] count) {
    ArrayList<ArrayList<Integer>> answer = new ArrayList<>();
    for (int i = 0; i < n; i++) {
        ArrayList<Integer> row = new ArrayList<>();
        for (int j = 0; j < m; j++) {
            row.add(-1);
        }
        answer.add(row);
