def findEnemies( n : int, m : int, k : int, col : List[int], count : List[int]) -> List[List[int]]:
    arr=[[-1]*(m) for _ in range(n)]
    ind=0
    for i in range(n):
        while count[i]>0:
            arr[i][col[ind]]=ind
            ind+=1
            count[i]-=1
    return arr

