public static int minimumOperationsToWriteY(int[][] grid) {
    int[] cnt1 = new int[3];
    int[] cnt2 = new int[3];
    int n = grid.length;
    int m = n / 2;
    for (int i = 0; i < m; i++) {
        cnt1[grid[i][i]]++;
        cnt1[grid[i][n - 1 - i]]++;
        for (int j = 0; j < n; j++) {
            if (j != i && j != n - 1 - i) {
                cnt2[grid[i][j]]++;
            }
        }
    }
    for (int i = m; i < n; i++) {
        cnt1[grid[i][m]]++;
        for (int j = 0; j < n; j++) {
            if (j != m) {
                cnt2[grid[i][j]]++;
            }
        }
    }
    int maxNotChange = 0;
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            if (i != j) {
                maxNotChange = Math.max(maxNotChange, cnt1[i] + cnt2[j]);
            }
        }
    }
    return n * n - maxNotChange;
}

