public static int paintWalls(int[] cost, int[] time) {
    int n = cost.length;
    int[] f = new int[n * 2 + 1];
    Arrays.fill(f, Integer.MAX_VALUE / 2);
    f[n] = 0;
    for (int i = 0; i < n; ++i) {
        int[] g = new int[n * 2 + 1];
        Arrays.fill(g, Integer.MAX_VALUE / 2);
        for (int j = 0; j <= n * 2; ++j) {

